function screenWidthCheck(){(window.innerWidth||document.documentElement.clientWidth)<1e3&&$("body").addClass("mobile")}function gameHall_JS(){$("a.contact").click((function(){$(".contact-menu").toggleClass("show")})),$("a.btn-tab.Flow").click((function(){$(".main").addClass("main-project").removeClass("main-wireframe")})),$("a.btn-tab.Wireframe").click((function(){$(".main").addClass("main-wireframe").removeClass("main-project"),$(".project").each((function(){0===$(this).find("img.wireframe").length&&$(this).css("display","none")}))})),$(".mobile nav").click((function(){$(this).toggleClass("active")}))}function initializeCarousel(){$("#myCarousel").carousel({interval:1e3,wrap:!1}),$("#myCarousel1").carousel({interval:2e3,wrap:!1}),$("#myCarousel2").carousel({interval:3e3,wrap:!1})}function timeLine(){function e(){var e=$(window).scrollTop()+1*$(window).height()/2;$(".timeline li").each((function(){$(this).offset().top<e?$(this).addClass("visible"):$(this).removeClass("visible")}))}$(document).on("scroll",e),e()}$(document).ready((function(){screenWidthCheck(),gameHall_JS(),timeLine(),initializeCarousel()}));